import React, { useState, useEffect, useRef } from 'react';
import "../../../assets/style/ServicesStyle/ChatServicesStyle/MessageOptionsMenu.css";
import ConfirmationPopup from './ConfirmationPopup';

const MessageOptions = ({ message, onReply, onCopy, onUnsendForMe, onForward, onBump, onPin, onMore, showMoreOptions, setShowMoreOptions, onClose }) => {
  const handleUnsendForMe = () => {
    onUnsendForMe(message);
    onClose(); // Close the options menu
  };

  return (
    <div className="message-options-wrapper">
      <div className={`message-options ${showMoreOptions ? 'show-more' : ''}`}>
        <div className="options-main">
          <button onClick={(e) => { onReply(e); onClose(); }}>Reply</button>
          <button onClick={(e) => { onCopy(e); onClose(); }}>Copy</button>
          <button onClick={handleUnsendForMe}>Unsend for me</button>
          <button className="more-button" onClick={() => setShowMoreOptions(true)}>More</button>
        </div>
        <div className="options-more">
          <button onClick={(e) => { onForward(e); onClose(); }}>Forward</button>
          <button onClick={(e) => { onBump(e); onClose(); }}>Bump</button>
          <button onClick={(e) => { onPin(e); onClose(); }}>Pin</button>
          <button onClick={() => setShowMoreOptions(false)}>Back</button>
        </div>
      </div>
    </div>
  );
};

const MessageOptionsMenu = ({ message, isMobile, onOptionClick }) => {
  const [showOptions, setShowOptions] = useState(false);
  const [showMoreOptions, setShowMoreOptions] = useState(false);
  const optionsRef = useRef(null);

  const toggleOptions = (e) => {
    e.stopPropagation();
    setShowOptions(prev => !prev);
    setShowMoreOptions(false);
  };

  const handleOptionClick = (option) => {
    onOptionClick(option, message);
    if (option !== 'more') {
      setShowOptions(false);
      setShowMoreOptions(false);
    }
  };

  const closeOptions = () => {
    setShowOptions(false);
    setShowMoreOptions(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (optionsRef.current && !optionsRef.current.contains(event.target)) {
        closeOptions();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const isUserMessage = message.sender === "user";

  return (
    <div className={`message-options-container ${isUserMessage ? 'user-message' : 'other-message'}`} ref={optionsRef}>
      <div className="message-content-wrapper">
        {/* Your message content goes here */}
        <div className="message-options-toggle-wrapper">
          {!isMobile && (
            <button 
              className={`message-options-toggle ${isUserMessage ? 'current-user' : 'other-user'}`}
              onClick={toggleOptions}
            >
              ⋮
            </button>
          )}
        </div>
      </div>
      {showOptions && (
        <MessageOptions
          message={message}
          onReply={() => handleOptionClick('reply')}
          onCopy={() => handleOptionClick('copy')}
          onUnsendForMe={(msg) => handleOptionClick('unsendForMe', msg)}
          onForward={() => handleOptionClick('forward')}
          onBump={() => handleOptionClick('bump')}
          onPin={() => handleOptionClick('pin')}
          onMore={() => handleOptionClick('more')}
          showMoreOptions={showMoreOptions}
          setShowMoreOptions={setShowMoreOptions}
          onClose={closeOptions}
        />
      )}
    </div>
  );
};

export default MessageOptionsMenu;
