// frontend/src/pages/Signup.jsx

import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import {
  handleSuccess,
  handleError,
  ToastContainer,
} from "../utils/ReactToastify";
import "../assets/style/ReactToastifyCustom.css";
import "../assets/style/animations.css";
import "../assets/style/loading.css";
import LoadingOverlay from '../components/LoadingOverlay';
import "../assets/style/Signup.css";
import logo from "../assets/images/logo.png";
function Signup() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    phone: "",
    dob: {
      day: "",
      month: "",
      year: "",
    },
    gender: "",
  });
  const [loading, setLoading] = useState(false);
  const [fadeIn, setFadeIn] = useState(true);
  const [fadeOut, setFadeOut] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setFadeIn(false);
    }, 500);
    return () => clearTimeout(timeoutId);
  }, []);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      handleError("Passwords do not match");
      return;
    }

    setLoading(true);
    setFadeOut(false);

    setTimeout(async () => {
      setFadeOut(true);
      setTimeout(async () => {
        try {
          const response = await fetch("http://localhost:8080/auth/signup", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              firstName: formData.firstName,
              lastName: formData.lastName,
              email: formData.email,
              password: formData.password,
              confirmPassword: formData.confirmPassword,
              phone: formData.phone,
              dob: formData.dob,
              gender: formData.gender,
            }),
          });

          const result = await response.json();
          if (response.ok) {
            handleSuccess("Signup successful! Please verify your email.");
            setTimeout(() => navigate('/verify-email', { 
              state: { 
                token: result.verificationToken,
                email: formData.email 
              } 
            }), 500);
          } else {
            if (result.errors) {
              const errorMessages = result.errors
                .map((err) => `${err.field}: ${err.message}`)
                .join(", ");
              handleError(errorMessages);
            } else {
              handleError(result.message);
            }
          }
        } catch (err) {
          handleError("Network error. Please check your connection and try again.");
        } finally {
          setLoading(false);
        }
      }, 250);
    }, 1000);
  };

  return (
<div className="signup-page-container">
      <img src={logo} alt="Logo" className="application-logo" />
      <div className="main-container">
        <h1>Create Your Account</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group-wrapper">
            <div className="single-form-group-wrapper">
              <input
                type="text"
                className="input-field"
                placeholder=" "
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                required
              />
              <label className="form-label">First Name</label>
            </div>
            <div className="single-form-group-wrapper">
              <input
                type="text"
                className="input-field"
                placeholder=" "
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                required
              />
              <label className="form-label">Last Name</label>
            </div>
          </div>
          <div className="signup-form-group-wrapper">
            <input
              type="email"
              className="input-field"
              placeholder=" "
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <label className="form-label">Email</label>
          </div>
          <div className="signup-form-group-wrapper">
            <input
              type="password"
              className="input-field"
              placeholder=" "
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <label className="form-label">Password</label>
          </div>
          <div className="signup-form-group-wrapper">
            <input
              type="password"
              className="input-field"
              placeholder=" "
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
            <label className="form-label">Confirm Password</label>
          </div>
          <div className="signup-form-group-wrapper">
            <input
              type="text"
              className="input-field phone-number-input"
              placeholder=" "
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
              required
            />
            <label className="form-label">Phone Number</label>
          </div>
          <div className="signup-form-group-wrapper">
            <input
              type="date"
              className="input-field"
              placeholder=" "
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleChange}
              required
            />
            <label className="form-label">Date of Birth</label>
          </div>
          <div className="signup-form-group-wrapper">
            <select
              className="input-field"
              name="gender"
              value={formData.gender}
              onChange={handleChange}
              required
            >
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
            <label className="form-label">Gender</label>
          </div>
          <button type="submit" className="button signup-submit-button">
            Sign Up
          </button>
          <span className="terms-text">
            By signing up, you agree to our{" "}
            <Link to="/terms" className="terms-navigation-link">
              Terms of Service
            </Link>{" "}
            and{" "}
            <Link to="/privacy" className="terms-navigation-link">
              Privacy Policy
            </Link>
            .
          </span>
          <div className="bottom-box-wrapper">
            <span className="text-span">Already have an account?</span>
            <Link to="/login" className="page-navigation-link">
              Login
            </Link>
          </div>
        </form>
        <ToastContainer />
      </div>
    </div>
  );
}

export default Signup;
