import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { ToastContainer } from "react-toastify";
import { handleError, handleSuccess } from "../utils";

function Signup() {
  const [signupInfo, setSignupInfo] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    reEnterPassword: "",
    phoneNumber: "",
    dateOfBirth: "",
    gender: "",
    agreement: false,
  });

  const navigate = useNavigate();
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const copySignupInfo = { ...signupInfo };
    copySignupInfo[name] = type === "checkbox" ? checked : value;
    setSignupInfo(copySignupInfo);
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    const {
      firstName,
      lastName,
      email,
      password,
      reEnterPassword,
      phoneNumber,
      dateOfBirth,
      gender,
      agreement,
    } = signupInfo;

    if (
      !firstName ||
      !lastName ||
      !email ||
      !password ||
      !reEnterPassword ||
      !phoneNumber ||
      !dateOfBirth ||
      !gender ||
      !agreement
    ) {
      return handleError("All fields are required");
    }

    try {
      const url = `http://localhost:8080/auth/signup`;
      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(signupInfo),
      });
      const result = await response.json();
      const { success, message, error } = result;
      if (success) {
        handleSuccess(message);
        setTimeout(() => {
          navigate("/login");
        }, 1000);
      } else if (error) {
        const details = error?.details[0].message;
        handleError(details);
      } else if (!success) {
        handleError(message);
      }
    } catch (err) {
      handleError(err);
    }
  };

  return (
    <div className="container">
      <img src="/favicon.ico" alt="Logo" className="logo" />
      <h1>Signup</h1>
      <form onSubmit={handleSignup}>
        <div className="form-group">
          <input
            onChange={handleChange}
            type="text"
            name="firstName"
            required
            placeholder=" " // Space to enable floating effect
            value={signupInfo.firstName}
            id="firstName"
            autoFocus
          />
          <label className="form-label" htmlFor="firstName">
            First Name
          </label>
        </div>
        <div className="form-group">
          <input
            onChange={handleChange}
            type="text"
            name="lastName"
            required
            placeholder=" " // Space to enable floating effect
            value={signupInfo.lastName}
            id="lastName"
          />
          <label className="form-label" htmlFor="lastName">
            Last Name
          </label>
        </div>
        <div className="form-group">
          <input
            onChange={handleChange}
            type="email"
            name="email"
            required
            placeholder=" " // Space to enable floating effect
            value={signupInfo.email}
            id="email"
          />
          <label className="form-label" htmlFor="email">
            Email
          </label>
        </div>
        <div className="form-group">
          <input
            onChange={handleChange}
            type="password"
            name="password"
            required
            placeholder=" " // Space to enable floating effect
            value={signupInfo.password}
            id="password"
          />
          <label className="form-label" htmlFor="password">
            Password
          </label>
        </div>
        <div className="form-group">
          <input
            onChange={handleChange}
            type="password"
            name="reEnterPassword"
            required
            placeholder=" " // Space to enable floating effect
            value={signupInfo.reEnterPassword}
            id="reEnterPassword"
          />
          <label className="form-label" htmlFor="reEnterPassword">
            Re-enter Password
          </label>
        </div>
        <div className="form-group">
          <input
            onChange={handleChange}
            type="text"
            name="phoneNumber"
            required
            placeholder=" " // Space to enable floating effect
            value={signupInfo.phoneNumber}
            id="phoneNumber"
          />
          <label className="form-label" htmlFor="phoneNumber">
            Phone Number
          </label>
        </div>

        <div className="form-group">
            <select
              onChange={handleChange}
              name="gender"
              required
              value={signupInfo.gender}
              id="gender"
            >
              <option value="" disabled>
                Select your gender
              </option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
        </div>

        <div className="form-group">
          <label>Date of Birth</label>
          <div className="dob-select">
            <select
              onChange={handleChange}
              name="dobDay"
              required
              value={signupInfo.dobDay}
              id="dobDay"
            >
              <option value="" disabled>
                Day
              </option>
              {[...Array(31)].map((_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>

            <select
              onChange={handleChange}
              name="dobMonth"
              required
              value={signupInfo.dobMonth}
              id="dobMonth"
            >
              <option value="" disabled>
                Month
              </option>
              {[...Array(12)].map((_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>

            <select
              onChange={handleChange}
              name="dobYear"
              required
              value={signupInfo.dobYear}
              id="dobYear"
            >
              <option value="" disabled>
                Year
              </option>
              {[...Array(100)].map((_, i) => (
                <option
                  key={new Date().getFullYear() - i}
                  value={new Date().getFullYear() - i}
                >
                  {new Date().getFullYear() - i}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="agreement">
            <input
              onChange={handleChange}
              type="checkbox"
              name="agreement"
              checked={signupInfo.agreement}
              id="agreement"
            />
            I agree to the terms and conditions
          </label>
          <button type="button">See more</button>
        </div>

        <button type="submit">Signup</button>
        <span>
          Already have an account?
          <Link to="/login" className="style-link">
            Login
          </Link>
        </span>
      </form>
      <ToastContainer />
    </div>
  );
}

export default Signup;
