// frontend/src/pages/Signup.jsx

import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import Modal from '../components/Modal';
import {
  handleSuccess,
  handleError,
  ToastContainer,
} from "../utils/ReactToastify";
import "../assets/style/styleutils/ReactToastifyCustom.css";
import '../assets/style/styleutils/loading.css';
import '../assets/style/styleutils/animations.css';
import LoadingOverlay from '../components/LoadingOverlay';
import "../assets/style/PagesStyle/Signup.css";
import StarIcon from '../components/StarIcon';

function Signup() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    phone: "",
    dob: { day: "", month: "", year: "" },
    gender: "",
  });
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevState) => ({ ...prevState, [id]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      handleError("Passwords do not match");
      return;
    }
    setLoading(true);
    setTimeout(async () => {
      try {
        const response = await fetch("http://localhost:8080/auth/signup", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData),
        });
        const result = await response.json();
        if (response.ok) {
          handleSuccess("Signup successful! Please verify your email.");
          setTimeout(() => navigate('/verify-email', { state: { token: result.verificationToken, email: formData.email } }), 500);
        } else {
          handleError(result.errors ? result.errors.map(err => `${err.field}: ${err.message}`).join(", ") : result.message);
        }
      } catch (err) {
        handleError("Network error. Please check your connection and try again.");
      } finally {
        setLoading(false);
      }
    }, 1000);
  };

  const openModal = (type) => {
    if (type === 'terms') {
      setModalContent(
        <>
          <h1>Terms of Use</h1>
          <p>Welcome to our application. By using our services, you agree to the following terms:</p>
          <p>1. **Acceptance of Terms**: By accessing or using our services, you agree to be bound by these terms.</p>
          <p>2. **User Responsibilities**: You are responsible for your account and all activities that occur under your account.</p>
          <p>3. **Content Ownership**: You retain ownership of any content you submit, but you grant us a license to use it.</p>
          <p>4. **Termination**: We may terminate or suspend your access if you violate these terms.</p>
          <p>For more details, please read our full terms of use.</p>
        </>
      );
    } else if (type === 'privacy') {
      setModalContent(
        <>
          <h1>Privacy Policy</h1>
          <p>Your privacy is important to us. This policy explains how we collect, use, and protect your information.</p>
          <p>1. **Information We Collect**: We collect information you provide directly, such as your name and email address.</p>
          <p>2. **How We Use Your Information**: We use your information to provide and improve our services.</p>
          <p>3. **Data Security**: We implement reasonable security measures to protect your information.</p>
          <p>4. **Your Rights**: You have the right to access, correct, or delete your personal information.</p>
          <p>For more details, please read our full privacy policy.</p>
        </>
      );
    }
    setIsModalOpen(true);
  };

  return (
    <div className="signup-page__container fade-in"> 
      <StarIcon width={48} height={48} fill="#5E5CE6" className="signup-page__app-icon" />
      <h1 className="signup-page__title">Sign up</h1>
      <form onSubmit={handleSubmit} className="signup-page__form">
        <div className="signup-page__form-group-container">
          <div className="signup-page__form-group">
            <input
              type="text"
              id="firstName"
              placeholder=""
              value={formData.firstName}
              onChange={handleChange}
              required
              className="signup-page__input"
            />
            <label htmlFor="firstName" className="signup-page__form-label">
              First Name
            </label>
          </div>
          <div className="signup-page__form-group">
            <input
              type="text"
              id="lastName"
              placeholder=""
              value={formData.lastName}
              onChange={handleChange}
              required
              className="signup-page__input"
            />
            <label htmlFor="lastName" className="signup-page__form-label">
              Last Name
            </label>
          </div>
        </div>

        <div className="signup-page__form-group">
          <input
            type="email"
            id="email"
            placeholder=""
            value={formData.email}
            onChange={handleChange}
            required
            className="signup-page__input"
          />
          <label htmlFor="email" className="signup-page__form-label">
            Email
          </label>
        </div>

        <div className="signup-page__form-group-container">
          <div className="signup-page__form-group">
            <input
              className="signup-page__input signup-page__phone-input"
              type="tel"
              id="phone"
              placeholder=""
              value={formData.phone}
              onChange={handleChange}
              required
            />
            <label htmlFor="phone" className="signup-page__form-label">
              Phone Number
            </label>
          </div>
          <div className="signup-page__form-group">
            <select
              id="gender"
              value={formData.gender}
              onChange={handleChange}
              required
              className="signup-page__select"
            >
              <option value="" disabled>
                Select Gender
              </option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
          </div>
        </div>

        <div className="signup-page__form-group-container">
          <div className="signup-page__form-group">
            <input
              type="password"
              id="password"
              placeholder=""
              value={formData.password}
              onChange={handleChange}
              required
              className="signup-page__input"
            />
            <label htmlFor="password" className="signup-page__form-label">
              Password
            </label>
          </div>
          <div className="signup-page__form-group">
            <input
              type="password"
              id="confirmPassword"
              placeholder=""
              value={formData.confirmPassword}
              onChange={handleChange}
              required
              className="signup-page__input"
            />
            <label htmlFor="confirmPassword" className="signup-page__form-label">
              Confirm Password
            </label>
          </div>
        </div>

        <div className="signup-page__form-group-container signup-page__dob-container">
          <div className="signup-page__form-group">
            <select
              id="dobDay"
              value={formData.dob.day}
              onChange={(e) =>
                setFormData((prevState) => ({
                  ...prevState,
                  dob: { ...prevState.dob, day: e.target.value },
                }))
              }
              required
              className="signup-page__select"
            >
              <option value="" disabled>
                Day
              </option>
              {Array.from({ length: 31 }, (_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>
          </div>
          <div className="signup-page__form-group">
            <select
              id="dobMonth"
              value={formData.dob.month}
              onChange={(e) =>
                setFormData((prevState) => ({
                  ...prevState,
                  dob: { ...prevState.dob, month: e.target.value },
                }))
              }
              required
              className="signup-page__select"
            >
              <option value="" disabled>
                Month
              </option>
              {Array.from({ length: 12 }, (_, i) => (
                <option key={i + 1} value={i + 1}>
                  {new Date(0, i).toLocaleString("default", { month: "long" })}
                </option>
              ))}
            </select>
          </div>
          <div className="signup-page__form-group">
            <select
              id="dobYear"
              value={formData.dob.year}
              onChange={(e) =>
                setFormData((prevState) => ({
                  ...prevState,
                  dob: { ...prevState.dob, year: e.target.value },
                }))
              }
              required
              className="signup-page__select"
            >
              <option value="" disabled>
                Year
              </option>
              {Array.from({ length: 100 }, (_, i) => (
                <option key={2024 - i} value={2024 - i}>
                  {2024 - i}
                </option>
              ))}
            </select>
          </div>
        </div>

        <button className="signup-page__submit-button" type="submit">
          Sign up
        </button>
        <span className="signup-page__login-link-container">
          Already have an account?{" "}
          <Link to="/login" className="signup-page__login-link">
            Login
          </Link>
        </span>
      </form>
      <div className="signup-page__terms-container">
        <span className="signup-page__terms">
          By creating an account, you agree to our
          <button onClick={() => openModal('terms')} className="signup-page__terms-link">Terms of Use</button>
          {" "}and
          <button onClick={() => openModal('privacy')} className="signup-page__terms-link">Privacy Policy</button>.
        </span>
      </div>
      <ToastContainer />
      <LoadingOverlay loading={loading} />
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} title="Terms and Conditions">
        {modalContent}
      </Modal>
    </div>
  );
}

export default Signup;
