import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [userInfo, setUserInfo] = useState(null);
    const [products, setProducts] = useState([]); // Initialize as an empty array
    const navigate = useNavigate();

    useEffect(() => {
        // Get logged in user info from local storage
        const user = JSON.parse(localStorage.getItem('loggedInUser'));
        setLoggedInUser(user?.first_name || ''); // Display the first name if available
        setUserInfo(user); // Set user info
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged Out');
        setTimeout(() => {
            navigate('/login');
        }, 1000);
    };

    const fetchProducts = async () => {
        try {
            const url = "http://localhost:8080/products";
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                }
            };
            const response = await fetch(url, headers);
            if (!response.ok) {
                throw new Error('Failed to fetch products');
            }
            const result = await response.json();
            setProducts(result);
        } catch (err) {
            handleError(err.message);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    return (
        <div>
            <h1>Welcome {loggedInUser}</h1>
            {userInfo && (
                <div>
                    <p><strong>First Name:</strong> {userInfo.first_name}</p>
                    <p><strong>Last Name:</strong> {userInfo.last_name}</p>
                    <p><strong>Email:</strong> {userInfo.email}</p>
                    <p><strong>Phone:</strong> {userInfo.phone}</p>
                    <p><strong>Date of Birth:</strong> {new Date(userInfo.dob).toLocaleDateString()}</p>
                    <p><strong>Username:</strong> {userInfo.username}</p>
                    <p><strong>Account Created:</strong> {new Date(userInfo.created_at).toLocaleString()}</p>
                    <p><strong>Last Updated:</strong> {new Date(userInfo.updated_at).toLocaleString()}</p>
                </div>
            )}
            <button onClick={handleLogout}>Logout</button>
            <div>
                {products.length > 0 ? (
                    products.map((item, index) => (
                        <ul key={index}>
                            <li>{item.name} : {item.price}</li>
                        </ul>
                    ))
                ) : (
                    <p>No products available</p>
                )}
            </div>
            <ToastContainer />
        </div>
    );
}

export default Home;
