import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../../utils/ReactToastify';
import '../../utils/ReactToastifyCustom.css';
import '../../utils/style/animations.css'; // Import animations.css
import '../../utils/style/FindUserForgotPassword.css';

function FindUserForgotPassword() {
    const [emailOrUsername, setEmailOrUsername] = useState('');
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const [fadeIn, setFadeIn] = useState(false); // Controls the fade-in effect
    const [showContent, setShowContent] = useState(false);
    const [suggestions, setSuggestions] = useState([]);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (fadeIn) {
            const timeoutId = setTimeout(() => {
                setFadeIn(false);
            }, 500);
            return () => clearTimeout(timeoutId);
        }
    }, [fadeIn]);

    useEffect(() => {
        if (loading) {
            const timerId = setTimeout(() => {
                setShowContent(true);
            }, 500); // Show content after 0.5 seconds
            return () => clearTimeout(timerId);
        } else {
            setShowContent(true); // Ensure content is shown if loading is false
        }
    }, [loading]);

    const handleSearch = async (event) => {
        event.preventDefault();
        setLoading(true);
        setShowContent(false); // Hide content while loading

        try {
            const response = await fetch('http://localhost:8080/verification/forgot-password/find-user', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ emailOrUsername }),
            });

            const result = await response.json();

            // Delay setting user state for 0.5 seconds to show loading spinner
            setTimeout(() => {
                if (response.ok) {
                    handleSuccess(result.message);
                    setUser(result.user);
                } else {
                    handleError(result.message);
                    setUser(null);
                }
                setFadeIn(true);
                setLoading(false);
            }, 500);
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
            setFadeIn(true);
            setLoading(false);
        }
    };

    const handleSendOtp = async () => {
        try {
            const response = await fetch('http://localhost:8080/verification/forgot-password/send-otp', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: user.email }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                navigate('/forgot-password/sent-otp', { state: { email: user.email } });
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Failed to send OTP. Please try again.');
        }
    };

    const handleProceed = () => {
        if (user) {
            handleSendOtp();
            navigate('/forgot-password/sent-otp', { state: { email: user.email } });
        }
    };

    const handleInputChange = (event) => {
        const value = event.target.value;
        setEmailOrUsername(value);
        updateSuggestions(value);
    };

    const updateSuggestions = (value) => {
        const suggestionList = [];
        const domains = ['gmail.com', 'yahoo.com', 'outlook.com', 'hotmail.com'];

        if (value.includes('@')) {
            const [, domain] = value.split('@');
            if (domain.length > 0) {
                domains.forEach((d) => {
                    if (d.startsWith(domain)) {
                        suggestionList.push(`${value.split('@')[0]}@${d}`);
                    }
                });
            }
        } else {
            domains.forEach((d) => {
                suggestionList.push(`${value}@${d}`);
            });
        }

        setSuggestions(suggestionList);
        setShowSuggestions(suggestionList.length > 0);
    };

    const handleSuggestionClick = (suggestion) => {
        setEmailOrUsername(suggestion);
        setShowSuggestions(false);
    };

    return (
        <div className="find-user-container container">
            <h1>Forgot Password</h1>
            <form onSubmit={handleSearch}>
                <div className="input-container">
                    <input
                        type="search"
                        placeholder="Email or Username"
                        value={emailOrUsername}
                        onChange={handleInputChange}
                        required
                    />
                    <button type="submit" disabled={loading} className="search-icon-wrapper">
                        <img src="/search.ico" alt="Search icon" className="search-icon-img" />
                    </button>
                    {showSuggestions && (
                        <ul className="suggestion-list">
                            {suggestions.map((suggestion, index) => (
                                <li key={index} onClick={() => handleSuggestionClick(suggestion)}>
                                    {suggestion}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            </form>


            <div className="confirmation-message">
                {user ? (<>
                </>
                ) : (
                    <div className="placeholder-message">
                        <p>Search your Account by Email Or Username.</p>
                    </div>
                )}

            </div>

            <div className="found-user-wrapper">
                {loading && !showContent ? (
                    <div className="loading-overlay">
                        <img src='/apple-loading.gif' alt='Loading spinner' className='loading-spinner' />
                    </div>
                ) : (
                    <div className={`found-user-content ${fadeIn ? 'fade-in-bottom' : ''}`}>
                        {user ? (
                            <>

                                <div
                                    className="user-card"
                                    onClick={handleProceed}
                                    style={{ cursor: 'pointer' }}
                                >
                                    <div className="user-card-image">
                                        <img src='/avater.png' alt='User avatar' className='profilePicture' />
                                    </div>
                                    <div className="user-card-details">
                                        <h3>{user.name}</h3>
                                        <p>{user.username}</p>
                                    </div>
                                </div>
                            </>
                        ) : (
                            <div className="placeholder-message">
                                <p>Search your Account by Email Or Username.</p>
                            </div>
                        )}
                    </div>
                )}
            </div>

            <ToastContainer />
        </div>
    );
}

export default FindUserForgotPassword;
