import React, { useState } from 'react';
import '../../../assets/style/ServicesStyle/ChatServicesStyle/ForwardPopup.css';
import ChatList from './ChatList';

const ForwardPopup = ({ onConfirm, onCancel }) => {
  const [selectedUsers, setSelectedUsers] = useState([]);

  const handleUserSelect = (user) => {
    setSelectedUsers(prevSelected => 
      prevSelected.some(u => u.id === user.id)
        ? prevSelected.filter(u => u.id !== user.id)
        : [...prevSelected, user]
    );
  };

  const handleConfirm = () => {
    onConfirm(selectedUsers);
  };

  return (
    <div className="forward-popup-overlay">
      <div className="forward-popup">
        <h2>Forward Message</h2>
        <ChatList
          selectedUser={null}
          handleUserSelect={handleUserSelect}
          multiSelect={true}
          selectedUsers={selectedUsers}
        />
        <div className="forward-buttons">
          <button className="cancel-button" onClick={onCancel}>Cancel</button>
          <button 
            className="confirm-button" 
            onClick={handleConfirm} 
            disabled={selectedUsers.length === 0}
          >
            Forward
          </button>
        </div>
      </div>
    </div>
  );
};

export default ForwardPopup;
