// frontend/src/components/VerifyCodeConfirmAccount.js

import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../../../utils/ReactToastify';
import '../../utils/ReactToastifyCustom.css';
import '../../utils/style/SentOtpForgotPassword.css';
import OtpInput from '../Services/OtpInput';

function VerifyCodeConfirmAccount() {
    const [otp, setOtp] = useState('');
    const [timer, setTimer] = useState(60);
    const [isResendEnabled, setIsResendEnabled] = useState(false);
    const [isComplete, setIsComplete] = useState(false);
    const [email, setEmail] = useState('');
    const navigate = useNavigate();
    const { state } = useLocation();

    useEffect(() => {
        if (state && state.email) {
            setEmail(state.email);
        } else {
            navigate('/login');
        }
    }, [state, navigate]);

    useEffect(() => {
        const countdown = timer > 0 ? setInterval(() => {
            setTimer((prev) => prev - 1);
        }, 1000) : null;

        if (timer === 0) {
            setIsResendEnabled(true);
        }

        return () => {
            if (countdown) clearInterval(countdown);
        };
    }, [timer]);

    const handleVerify = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch("http://localhost:8080/verification/verify-code", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, code: otp }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess('Verification successful! Redirecting to home...');
                setTimeout(() => navigate('/home'), 500);
            } else {
                handleError(result.message || 'Verification failed. Please try again.');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    return (
        <div className="verification-container">
            <h1>Verify Your Account</h1>
            <form onSubmit={handleVerify}>
                <OtpInput
                    length={6}
                    onOtpSubmit={(otp) => setOtp(otp)}
                    onOtpComplete={(complete) => setIsComplete(complete)}
                />
                <div className="otp-buttons">
                    {timer === 0 ? (
                        <button
                            type="button"
                            className="otp-btn resend"
                            onClick={() => {}}
                        >
                            Resend Code
                        </button>
                    ) : (
                        <p>Time Remaining: {timer} s</p>
                    )}
                    <button
                        type="submit"
                        className={`otp-btn verify ${isComplete ? 'active' : ''}`}
                        disabled={!isComplete}
                    >
                        Verify Code
                    </button>
                </div>
            </form>
            <ToastContainer />
        </div>
    );
}

export default VerifyCodeConfirmAccount;
