// pages/FindUserForgotPassword.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess, ToastContainer } from '../utils/ReactToastify';

function FindUserForgotPassword() {
    const [emailOrUsername, setEmailOrUsername] = useState('');
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);

        try {
            const response = await fetch('http://localhost:8080/auth/find-user', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ emailOrUsername }),
            });
            const result = await response.json();
            if (response.ok) {
                setUser(result.user);
                handleSuccess('User found! Click "Proceed" to continue.');
            } else {
                handleError(result.message || 'User not found. Please try again.');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleProceed = () => {
        navigate('/sent-otp');
    };

    return (
        <div className="find-usercontainer">
            <h1>Find Your Account</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Enter your email or username..."
                    value={emailOrUsername}
                    onChange={(e) => setEmailOrUsername(e.target.value)}
                    required
                />
                <button type="submit" disabled={loading}>Search</button>
            </form>
            {user && (
                <div className="user-details">
                    <img src={user.avatar} alt="User Avatar" className="user-avatar" />
                    <p><strong>Email:</strong> {user.email}</p>
                    <p><strong>Username:</strong> {user.username}</p>
                    <button onClick={handleProceed}>Proceed</button>
                </div>
            )}
            <ToastContainer />
        </div>
    );
}

export default FindUserForgotPassword;
