// export default Home


document.addEventListener('DOMContentLoaded', () => {
    const logoutBtn = document.getElementById('logout-btn');

    logoutBtn.addEventListener('click', () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        window.location.href = '/login.html';
    });

    fetchChatList();
});

async function fetchChatList() {
    try {
        const response = await fetch('http://localhost:8080/chats', {
            headers: {
                'Authorization': localStorage.getItem('token')
            }
        });
        const chatList = await response.json();
        const chatListContainer = document.getElementById('chatList');
        
        chatList.forEach(chat => {
            const chatItem = document.createElement('li');
            chatItem.innerHTML = `<div class="chat-item-list-wrapper" onclick="openChat('${chat.name}')">
                                    <img src="./Assets/Images/avater/${chat.avatar}.png" alt="User Image" class="chat-user-avatar-image">
                                    <div class="chat-info-wrapper">
                                        <h2>${chat.name}</h2>
                                        <p>${chat.lastMessage}</p>
                                    </div>
                                  </div>`;
            chatListContainer.appendChild(chatItem);
        });
    } catch (err) {
        console.error('Error fetching chat list:', err);
    }
}

function openChat(chatName) {
    // Implement chat opening functionality
    console.log('Opening chat with:', chatName);
}
