import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { ToastContainer } from "react-toastify";
import { handleError, handleSuccess } from "../utils";

function Signup() {
  const [signupInfo, setSignupInfo] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    reEnterPassword: "",
    phoneNumber: "",
    dobDay: "",
    dobMonth: "",
    dobYear: "",
    gender: "",
    agreement: false,
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setSignupInfo((prevInfo) => ({
      ...prevInfo,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    const {
      firstName,
      lastName,
      email,
      password,
      reEnterPassword,
      phoneNumber,
      dobDay,
      dobMonth,
      dobYear,
      gender,
      agreement,
    } = signupInfo;
  
    // Validate form fields before sending the request
    if (!firstName) return handleError("First name is required.");
    if (!lastName) return handleError("Last name is required.");
    if (!email) return handleError("Email is required.");
    if (!password) return handleError("Password is required.");
    if (!reEnterPassword) return handleError("Please re-enter your password.");
    if (password !== reEnterPassword) return handleError("Passwords do not match.");
    if (!phoneNumber) return handleError("Phone number is required.");
    if (!dobDay || !dobMonth || !dobYear) return handleError("Date of birth is required.");
    if (!gender) return handleError("Gender is required.");
    if (!agreement) return handleError("You must agree to the terms and conditions.");
  
    // Create the date of birth in the format YYYY-MM-DD
    const dateOfBirth = new Date(`${dobYear}-${dobMonth}-${dobDay}`);
  
    try {
      const url = `http://localhost:8080/auth/signup`;
      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
            firstName: firstName,     
            lastName: lastName,        
            email: email,              
            password: password,       
            phoneNumber: phoneNumber,  
            gender: gender,            
            agreement: agreement,   
            dateOfBirth: dateOfBirth   
        }),
      });
  
      const result = await response.json();
      const { success, message, error } = result;
  
      if (success) {
        handleSuccess(message);
        setTimeout(() => {
          navigate("/login");
        }, 1000);
      } else {
        const details = error?.details[0]?.message || error.message || message;
        handleError(details);
      }
    } catch (err) {
      handleError(err.message);
    }
  };
  

  return (
    <div className="container">
      <img src="/favicon.ico" alt="Logo" className="logo" />
      <h1>Sign Up</h1>
      <form onSubmit={handleSignup}>
        <div className="group">
          <div className="form-group">
            <input
              onChange={handleChange}
              type="text"
              name="firstName"
              required
              placeholder=" "
              value={signupInfo.firstName}
              id="firstName"
              autoFocus
            />
            <label className="form-label" htmlFor="firstName">
              First Name
            </label>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange}
              type="text"
              name="lastName"
              required
              placeholder=" "
              value={signupInfo.lastName}
              id="lastName"
            />
            <label className="form-label" htmlFor="lastName">
              Last Name
            </label>
          </div>
        </div>

        <div className="form-group">
          <input
            onChange={handleChange}
            type="email"
            name="email"
            required
            placeholder=" "
            value={signupInfo.email}
            id="email"
          />
          <label className="form-label" htmlFor="email">
            Email
          </label>
        </div>

        <div className="group">
          <div className="form-group">
            <input
              onChange={handleChange}
              type="password"
              name="password"
              required
              placeholder=" "
              value={signupInfo.password}
              id="password"
            />
            <label className="form-label" htmlFor="password">
              Password
            </label>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange}
              type="password"
              name="reEnterPassword"
              required
              placeholder=" "
              value={signupInfo.reEnterPassword}
              id="reEnterPassword"
            />
            <label className="form-label" htmlFor="reEnterPassword">
              Re-enter Password
            </label>
          </div>
        </div>

        <div className="group">
          <div className="form-group">
            <input
              onChange={handleChange}
              type="text"
              name="phoneNumber"
              required
              placeholder=" "
              value={signupInfo.phoneNumber}
              id="phoneNumber"
            />
            <label className="form-label" htmlFor="phoneNumber">
              Phone Number
            </label>
          </div>

          <div className="form-group">
            <select
              onChange={handleChange}
              name="gender"
              required
              value={signupInfo.gender}
              id="gender"
            >
              <option value="" disabled>
                Select your gender
              </option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
          </div>
        </div>

        <div className="form-group">
          <label>Date of Birth</label>
          <div className="dob-select">
            <select
              onChange={handleChange}
              name="dobDay"
              required
              value={signupInfo.dobDay}
              id="dobDay"
            >
              <option value="" disabled>
                Day
              </option>
              {[...Array(31)].map((_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>

            <select
              onChange={handleChange}
              name="dobMonth"
              required
              value={signupInfo.dobMonth}
              id="dobMonth"
            >
              <option value="" disabled>
                Month
              </option>
              {[...Array(12)].map((_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>

            <select
              onChange={handleChange}
              name="dobYear"
              required
              value={signupInfo.dobYear}
              id="dobYear"
            >
              <option value="" disabled>
                Year
              </option>
              {[...Array(100)].map((_, i) => (
                <option
                  key={new Date().getFullYear() - i}
                  value={new Date().getFullYear() - i}
                >
                  {new Date().getFullYear() - i}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="group">
          <div className="form-group">
            <label htmlFor="agreement">
              <input
                onChange={handleChange}
                type="checkbox"
                name="agreement"
                checked={signupInfo.agreement}
                id="agreement"
              />
              I agree to the terms and conditions
            </label>
          </div>
        </div>

        <div className="form-group">
          <button type="submit" className="signup-button">
            Sign Up
          </button>
        </div>

        <div className="form-group">
          <span className="login-prompt">
            Already have an account?{" "}
            <Link to="/login" className="login-link">
              Log In
            </Link>
          </span>
        </div>
        
      </form>
      <ToastContainer />
    </div>
  );
}

export default Signup;
