import React, { useCallback, useRef, useEffect } from 'react';
import MessageReactions from './MessageReactions';
import MessageOptionsMenu from './MessageOptionsMenu';
import ConfirmationPopup from './ConfirmationPopup';
import ScrollButtons from './ScrollButtons';
import '../../../assets/style/ServicesStyle/ChatServicesStyle/SelectedUserMessages.css';

const SelectedUserMessages = ({ selectedUser, messages, formatTime, reactions, showReactions, handleReaction, toggleReactionMenu, onEditMessage }) => {
  const containerRef = useRef(null);

  const renderMessageGroups = useCallback(() => {
    // ... existing renderMessageGroups logic
  }, [messages, selectedUser, formatTime, reactions, showReactions, handleReaction, toggleReactionMenu, onEditMessage]);

  useEffect(() => {
    // Scroll to bottom when new messages are added
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="chat-messages-container" ref={containerRef}>
      {renderMessageGroups()}
      <ScrollButtons containerRef={containerRef} />
      {/* ... other components like ConfirmationPopup */}
    </div>
  );
};

export default SelectedUserMessages;
