// App.js
import { Navigate, Route, Routes } from 'react-router-dom';
import { useState } from 'react';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Home from './pages/Home';
import FindUserForgotPassword from './components/FindUserForgotPassword';
import SentOtpForgotPassword from './components/SentOtpForgotPassword';
import ResetPassword from './components/ResetPassword';
import RefrshHandler from './utils/RefreshHandler';
import 'react-toastify/dist/ReactToastify.css';



function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    const PrivateRoute = ({ element }) => {
        return isAuthenticated ? element : <Navigate to="/login" />
    }

    return (
        <div className="App">
            <RefrshHandler setIsAuthenticated={setIsAuthenticated} />
            <Routes>
                <Route path='/' element={<Navigate to="/login" />} />
                <Route path='/login' element={<Login />} />
                <Route path='/signup' element={<Signup />} />
                <Route path='/home' element={<PrivateRoute element={<Home />} />} />
                <Route path='/find-user' element={<FindUserForgotPassword />} />
                <Route path='/sent-otp' element={<SentOtpForgotPassword />} />
                <Route path='/reset-password' element={<ResetPassword />} />
            </Routes>
        </div>
    );
}

export default App;
