// Signup.js

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';


function Signup() {
    const [signupInfo, setSignupInfo] = useState({
        name: '',
        email: '',
        password: ''
    });

    const [loading, setLoading] = useState(false); // Add loading state
    const [loadingCompleted, setLoadingCompleted] = useState(false); // Track loading completion
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        const copySignupInfo = { ...signupInfo };
        copySignupInfo[name] = value;
        setSignupInfo(copySignupInfo);
    }

    // const handleSignup = async (e) => {
    //     e.preventDefault();
    //     const { name, email, password } = signupInfo;
    //     if (!name || !email || !password) {
    //         return handleError('Name, email, and password are required');
    //     }
    //     setLoading(true); // Start loading
    //     setLoadingCompleted(false); // Reset loading completion state

    //     try {
    //         const url = `http://localhost:8080/auth/signup`;
    //         const response = await fetch(url, {
    //             method: "POST",
    //             headers: {
    //                 'Content-Type': 'application/json'
    //             },
    //             body: JSON.stringify(signupInfo)
    //         });
    //         const result = await response.json();
    //         const { success, message, error } = result;

    //         // Simulate a delay to show the loading spinner
    //         setTimeout(() => {
    //             setLoading(false);
    //             setLoadingCompleted(true);
    //             if (success) {
    //                 handleSuccess(message);
    //                 setTimeout(() => {
    //                     navigate('/login');
    //                 }, 1000);
    //             } else if (error) {
    //                 const details = error?.details[0].message;
    //                 handleError(details);
    //             } else {
    //                 handleError(message);
    //             }
    //         }, 1000); // 1 second delay for loading spinner

    //     } catch (err) {
    //         setLoading(false);
    //         setLoadingCompleted(true);
    //         handleError(err.message || 'An error occurred');
    //     }
    // }

    return (
        <div className='container'>
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Sign up</h1>
            <form onSubmit={handleSignup}>
                <div className='form-group'>
                    <input
                        onChange={handleChange}
                        type='text'
                        name='name'
                        id='name'
                        autoFocus
                        placeholder='Enter your name...'
                        value={signupInfo.name}
                    />
                    <label htmlFor='name' className='form-label'>Name</label>
                </div>
                <div className='form-group'>
                    <input
                        onChange={handleChange}
                        type='email'
                        name='email'
                        id='email'
                        placeholder='Enter your email...'
                        value={signupInfo.email}
                    />
                    <label htmlFor='email' className='form-label'>Email</label>
                </div>
                <div className='form-group'>
                    <input
                        onChange={handleChange}
                        type='password'
                        name='password'
                        id='password'
                        placeholder='Enter your password...'
                        value={signupInfo.password}
                    />
                    <label htmlFor='password' className='form-label'>Password</label>
                </div>
                <button type='submit'>Signup</button>
                <span>Already have an account? <Link to="/login" className='signup-link'>Login</Link></span>
            </form>
            <ToastContainer />
            
            {loading && (
                <div className={`loading-overlay ${loadingCompleted ? 'hidden' : ''}`}>
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            )}
        </div>
    );
}

export default Signup;
