// App.js
import { Navigate, Route, Routes } from 'react-router-dom';
import { useState } from 'react';
import Login from './Pages/Login';
import Signup from './Pages/Signup';
import Home from './Pages/Home';
import FindUserForgotPassword from './components/ForgotPassword/FindUserForgotPassword';
import SentOtpForgotPassword from './components/ForgotPassword/VerificationCodeForgotPassword';
import ResetPassword from './components/ForgotPassword/ResetPassword';
import RefrshHandler from './utils/RefreshHandler';
import ProtectedRoute from './utils/ProtectedRoute';
import { ForgotPasswordProvider } from './forgotPasswordState';
import 'react-toastify/dist/ReactToastify.css';
import VerifyCodeConfirmAccount from './components/EmailVerification/VerifyCodeConfirmAccount'; 

// For Debugging..
import { useLocation } from 'react-router-dom';

function App() {
    // For Debugging..
    const location = useLocation();
    console.log('Current location:', location.pathname);
    // For Debugging..

    const [isAuthenticated, setIsAuthenticated] = useState(false);
    console.log('Is authenticated:', isAuthenticated);

    return (
        <ForgotPasswordProvider>
            <div className="App">
                <RefrshHandler setIsAuthenticated={setIsAuthenticated} />
                <Routes>
                    <Route path='/' element={<Navigate to="/login" />} />
                    <Route path='/login' element={<Login />} />
                    <Route path='/signup' element={<Signup />} />
                    <Route path="/verify-email" element={
                        <ProtectedRoute 
                            element={VerifyCodeConfirmAccount} 
                            isAuthenticated={isAuthenticated} 
                        />
                    } />
                    <Route path='/home' element={
                        <ProtectedRoute 
                            element={Home} 
                            isAuthenticated={isAuthenticated} 
                        />
                    } />
                    <Route path='/find-user' element={<FindUserForgotPassword />} />
                    <Route path='/forgot-password/verification-code' element={
                        <ProtectedRoute 
                            element={SentOtpForgotPassword} 
                            isAuthenticated={state.userFound} 
                        />
                    } />
                    <Route path='/forgot-password/reset-password' element={
                        <ProtectedRoute 
                            element={ResetPassword} 
                            isAuthenticated={state.codeVerified} 
                        />
                    } />
                </Routes>
            </div>
        </ForgotPasswordProvider>
    );
}

export default App;
