// frontend/src/pages/Signup.jsx

import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import {
  handleSuccess,
  handleError,
  ToastContainer,
} from "../utils/ReactToastify";
import "../assets/style/ReactToastifyCustom.css";
import "../assets/style/animations.css";
import "../assets/style/loading.css";
import LoadingOverlay from '../components/LoadingOverlay';
import "../assets/style/Signup.css";

function Signup() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    phone: "",
    dob: {
      day: "",
      month: "",
      year: "",
    },
    gender: "",
  });
  const [loading, setLoading] = useState(false);
  const [fadeIn, setFadeIn] = useState(true);
  const [fadeOut, setFadeOut] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setFadeIn(false);
    }, 500);
    return () => clearTimeout(timeoutId);
  }, []);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      handleError("Passwords do not match");
      return;
    }

    setLoading(true);
    setFadeOut(false);

    setTimeout(async () => {
      setFadeOut(true);
      setTimeout(async () => {
        try {
          const response = await fetch("http://localhost:8080/auth/signup", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              firstName: formData.firstName,
              lastName: formData.lastName,
              email: formData.email,
              password: formData.password,
              confirmPassword: formData.confirmPassword,
              phone: formData.phone,
              dob: formData.dob,
              gender: formData.gender,
            }),
          });

          const result = await response.json();
          if (response.ok) {
            // Instead of setting tokens and navigating to home, redirect to verify code page
            handleSuccess("Signup successful! Please verify your email.");
            setTimeout(() => navigate('/verify-email', { state: { token: result.verificationToken } }), 500);
          } else {
            if (result.errors) {
              const errorMessages = result.errors
                .map((err) => `${err.field}: ${err.message}`)
                .join(", ");
              handleError(errorMessages);
            } else {
              handleError(result.message);
            }
          }
        } catch (err) {
          handleError("Network error. Please check your connection and try again.");
        } finally {
          setLoading(false);
        }
      }, 250);
    }, 1000);
  };

  useEffect(() => {
    // Add this new effect to initialize custom select dropdowns
    initializeCustomSelects();
  }, []);

  // Add this new function to initialize custom select dropdowns
  const initializeCustomSelects = () => {
    const selects = document.querySelectorAll("select");
    selects.forEach((select) => {
      const wrapper = document.createElement("div");
      wrapper.className = "custom-select";
      select.parentNode.insertBefore(wrapper, select);
      wrapper.appendChild(select);

      const customSelect = document.createElement("div");
      customSelect.className = "select-selected";
      customSelect.textContent = select.options[select.selectedIndex].textContent;
      wrapper.appendChild(customSelect);

      const optionsList = document.createElement("div");
      optionsList.className = "select-items select-hide";
      for (let i = 1; i < select.options.length; i++) {
        const opt = document.createElement("div");
        opt.textContent = select.options[i].textContent;
        opt.addEventListener("click", function() {
          select.selectedIndex = i;
          customSelect.textContent = this.textContent;
          const event = new Event('change');
          select.dispatchEvent(event);
        });
        optionsList.appendChild(opt);
      }
      wrapper.appendChild(optionsList);

      customSelect.addEventListener("click", function(e) {
        e.stopPropagation();
        closeAllSelect(this);
        this.nextSibling.classList.toggle("select-hide");
        this.classList.toggle("select-arrow-active");
      });
    });

    document.addEventListener("click", closeAllSelect);
  };

  const closeAllSelect = (elmnt) => {
    const items = document.getElementsByClassName("select-items");
    const selected = document.getElementsByClassName("select-selected");
    for (let i = 0; i < selected.length; i++) {
      if (elmnt !== selected[i]) {
        selected[i].classList.remove("select-arrow-active");
      }
    }
    for (let i = 0; i < items.length; i++) {
      if (elmnt !== items[i]) {
        items[i].classList.add("select-hide");
      }
    }
  };

  return (
    <div className={`signup-container container ${fadeIn ? "fade-in" : ""}`}>
      <img src="/images/icon/app-icon.ico" alt="App Icon" className="app-icon" />
      <h1>Sign up</h1>
      <form onSubmit={handleSubmit}>
        {/* Form fields and labels */}
        <div className="form-group-container">
          <div className="singup-form-group form-group">
            <input
              type="text"
              id="firstName"
              placeholder=""
              value={formData.firstName}
              onChange={handleChange}
              required
            />
            <label htmlFor="firstName" className="form-label">
              First Name
            </label>
          </div>
          <div className="singup-form-group form-group">
            <input
              type="text"
              id="lastName"
              placeholder=""
              value={formData.lastName}
              onChange={handleChange}
              required
            />
            <label htmlFor="lastName" className="form-label">
              Last Name
            </label>
          </div>
        </div>

        <div className="singup-form-group form-group">
          <input
            type="email"
            id="email"
            placeholder=""
            value={formData.email}
            onChange={handleChange}
            required
          />
          <label htmlFor="email" className="form-label">
            Email
          </label>
        </div>

        <div className="form-group-container">
          <div className="form-group">
            <input
              className="phone-input"
              type="tel"
              id="phone"
              placeholder=""
              value={formData.phone}
              onChange={handleChange}
              required
            />
            <label htmlFor="phone" className="form-label">
              Phone Number
            </label>
          </div>
          <div className="form-group">
            <select
              id="gender"
              value={formData.gender}
              onChange={handleChange}
              required
            >
              <option value="" disabled>
                Select Gender
              </option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
          </div>
        </div>

        <div className="form-group-container">
          <div className="form-group">
            <input
              type="password"
              id="password"
              placeholder=""
              value={formData.password}
              onChange={handleChange}
              required
            />
            <label htmlFor="password" className="form-label">
              Password
            </label>
          </div>
          <div className="form-group">
            <input
              type="password"
              id="confirmPassword"
              placeholder=""
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
            <label htmlFor="confirmPassword" className="form-label">
              Confirm Password
            </label>
          </div>
        </div>

        <div className="form-group-container dob">
          <div className="form-group">
            <select
              id="dobDay"
              value={formData.dob.day}
              onChange={(e) =>
                setFormData((prevState) => ({
                  ...prevState,
                  dob: { ...prevState.dob, day: e.target.value },
                }))
              }
              required
            >
              <option value="" disabled>
                Day
              </option>
              {Array.from({ length: 31 }, (_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <select
              id="dobMonth"
              value={formData.dob.month}
              onChange={(e) =>
                setFormData((prevState) => ({
                  ...prevState,
                  dob: { ...prevState.dob, month: e.target.value },
                }))
              }
              required
            >
              <option value="" disabled>
                Month
              </option>
              {Array.from({ length: 12 }, (_, i) => (
                <option key={i + 1} value={i + 1}>
                  {new Date(0, i).toLocaleString("default", { month: "long" })}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <select
              id="dobYear"
              value={formData.dob.year}
              onChange={(e) =>
                setFormData((prevState) => ({
                  ...prevState,
                  dob: { ...prevState.dob, year: e.target.value },
                }))
              }
              required
            >
              <option value="" disabled>
                Year
              </option>
              {Array.from({ length: 100 }, (_, i) => (
                <option key={2024 - i} value={2024 - i}>
                  {2024 - i}
                </option>
              ))}
            </select>
          </div>
        </div>

        <button className="signup-button" type="submit">
          Sign up
        </button>
        <span>
          Already have an account?{" "}
          <Link to="/login" className="pages-link">
            Login
          </Link>
        </span>
      </form>
      <span className="terms">
        By creating an account, you agree to our
        <Link to="/terms" className="terms-pages-link">
          {" "}
          Terms of Use
        </Link>
        ,
        <Link to="/privacy-policy" className="terms-pages-link">
          {" "}
          Privacy Policy
        </Link>
        .
      </span>
      <ToastContainer />
      <LoadingOverlay loading={loading} fadeOut={fadeOut} />
    </div>
  );
}

export default Signup;
