// Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

function Signup() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [fadeOut, setFadeOut] = useState(false); // Ensure fadeOut is defined
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setFadeOut(false);

        try {
            const response = await fetch('http://localhost:8080/auth/signup', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ email, password })
            });
            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message || 'Signup successful');
                navigate('/login');
            } else {
                handleError(result.message || 'An error occurred');
            }
        } catch (err) {
            handleError(err.message || 'An error occurred');
        } finally {
            setFadeOut(true);
            setTimeout(() => {
                // Optionally perform additional actions
            }, 250);
        }
    };

    return (
        <div className='container'>
            <h1>Signup</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Email:
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </label>
                <label>
                    Password:
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </label>
                <button type="submit">
                    Signup
                </button>
            </form>
            <ToastContainer />
        </div>
    );
}

export default Signup;
