// Home.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';


function Home() {
    const [loggedInUser, setLoggedInUser] = useState({});
    const [loading, setLoading] = useState(false);
    const [fadeOut, setFadeOut] = useState(false);
    const [fadeIn, setFadeIn] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchLoggedInUser();

        const timeoutId = setTimeout(() => {
            setFadeIn(false);
        }, 500); 

        return () => clearTimeout(timeoutId);
    }, []);

    const handleLogout = async () => {
        setLoading(true);
        setFadeOut(false);

        setTimeout(async () => {
            setFadeOut(true);
            setTimeout(async () => {
                try {
                    const response = await fetch("http://localhost:8080/auth/logout", {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                            'Authorization': `Bearer ${localStorage.getItem('token')}`
                        },
                        body: JSON.stringify({ refreshToken: localStorage.getItem('refreshToken') })
                    });
                    if (response.ok) {
                        localStorage.removeItem('token');
                        localStorage.removeItem('refreshToken');
                        localStorage.removeItem('loggedInUser');
                        handleSuccess('Logged out successfully! Redirecting...');
                        setTimeout(() => navigate('/login'), 500);
                    } else {
                        handleError('Failed to log out. Please try again.');
                    }
                } catch (err) {
                    handleError('Network error. Please check your connection and try again.');
                }
            }, 250);
        }, 1000);
    };

    const fetchLoggedInUser = async () => {
        setLoading(true);
        try {
            const url = "http://localhost:8080/Users/me";
            const headers = {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            };
            const response = await fetch(url, headers);
            const result = await response.json();
            if (response.ok) {
                setLoggedInUser(result);
                setLoading(false);
            } else {
                handleError(result.message || 'Failed to fetch user data. Please try again.');
                setLoading(false);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
            setLoading(false);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className={`home-container container ${fadeIn ? 'fade-in' : ''}`}>
            <h1>Welcome {loggedInUser.name}</h1>
            <div>
                {loading ? (
                    <div className={`loading-overlay ${fadeOut ? 'hidden' : ''}`}>
                        <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                    </div>
                ) : (
                    <ul>
                        img
                        <li>Name: {loggedInUser.name}</li>
                        <li>Name: {loggedInUser.name}</li>
                        <li>Email: {loggedInUser.email}</li>
                        <li>Username: {loggedInUser.username}</li>
                        <li>Joined: {new Date(loggedInUser.createdAt).toLocaleDateString()}</li>
                         <br/>
                    </ul>
                )}
            </div>

            <button onClick={handleLogout}>Logout</button>

            <ToastContainer />
        </div>
    );
}

export default Home;
