import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError } from '../utils';

function Home() {
    const [userInfo, setUserInfo] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserInfo = async () => {
            const token = localStorage.getItem('token');

            if (!token) {
                handleError('No token provided. Please log in again.');
                navigate('/login'); // Redirect to login if token is missing
                return;
            }

            try {
                const response = await fetch('http://localhost:8080/user/me', {
                    method: 'GET',
                    headers: {
                        'Authorization': token,
                        'Content-Type': 'application/json',
                    },
                });

                if (response.ok) {
                    const result = await response.json();
                    setUserInfo(result.user); // Set the user information
                } else {
                    handleError('Failed to fetch user info');
                }
            } catch (error) {
                console.error('Error fetching user info:', error);
                handleError('An error occurred while fetching user info');
            }
        };

        fetchUserInfo();
    }, [navigate]);

    return (
        <div className='container'>
            <h1>Welcome {userInfo ? userInfo.first_name : 'User'}</h1>
            {userInfo && (
                <div>
                    <p><strong>First Name:</strong> {userInfo.first_name}</p>
                    <p><strong>Last Name:</strong> {userInfo.last_name}</p>
                    <p><strong>Email:</strong> {userInfo.email}</p>
                    <p><strong>Phone:</strong> {userInfo.phone}</p>
                    <p><strong>Date of Birth:</strong> {new Date(userInfo.dob).toLocaleDateString()}</p>
                    <p><strong>Username:</strong> {userInfo.username}</p>
                    <p><strong>Account Created:</strong> {new Date(userInfo.created_at).toLocaleString()}</p>
                    <p><strong>Last Updated:</strong> {new Date(userInfo.updated_at).toLocaleString()}</p>
                </div>
            )}
            <button onClick={handleLogout}>Logout</button>
            <ToastContainer />
        </div>
    );
}

export default Home;
