import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../../utils/ReactToastifyCustom.css';
import '../../utils/style/profile.css';
import UserInfo from './UserInfo';
import AccountSettings from './AccountSettings';
import Notifications from './Notifications';
import Privacy from './Privacy';
import DataStorage from './DataStorage';
import Appearance from './Appearance';


function UserProfile() {
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(true);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [notifications, setNotifications] = useState({
    email: true,
    push: true,
    sms: false,
  });

  // State to manage the visibility of different sections
  const [showUserInfo, setShowUserInfo] = useState(false);
  const [showAccountSettings, setShowAccountSettings] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [showPrivacy, setShowPrivacy] = useState(false);
  const [showDataStorage, setShowDataStorage] = useState(false);
  const [showAppearance, setShowAppearance] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:8080/Users/me', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const result = await response.json();
      if (response.ok) {
        setUser(result);
      } else {
        handleError(result.message || 'Failed to fetch user data. Please try again.');
        navigate('/login');
      }
    } catch (err) {
      handleError('Network error. Please check your connection and try again.');
      navigate('/login');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      const response = await fetch('http://localhost:8080/auth/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({
          refreshToken: localStorage.getItem('refreshToken'),
        }),
      });
      if (response.ok) {
        localStorage.removeItem('token');
        localStorage.removeItem('refreshToken');
        localStorage.removeItem('loggedInUser');
        handleSuccess('Logged out successfully! Redirecting...');
        setTimeout(() => navigate('/login'), 500);
      } else {
        handleError('Failed to log out. Please try again.');
      }
    } catch (err) {
      handleError('Network error. Please check your connection and try again.');
    }
  };

  const openLogoutModal = () => setShowLogoutModal(true);
  const closeLogoutModal = () => setShowLogoutModal(false);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  const handleNotificationChange = (type) => {
    setNotifications((prev) => ({
      ...prev,
      [type]: !prev[type],
    }));
  };

  return (
    <div className={`profile-container ${darkMode ? 'dark-mode' : ''}`}>
      {loading ? (
        <div className="loading-overlay">
          <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
        </div>
      ) : (
        <>
          <button className="back-button icon-button" onClick={() => navigate(-1)}>
            <img src="/back-icon.png" alt="Back" />
          </button>
          <button className="edit-button icon-button" onClick={() => navigate('/edit-profile')}>
            <img src="/edit-icon.png" alt="Edit" />
          </button>
          <div className="user-info">
            <div className="user-avatar">
              <img src="/avatar.png" alt="Avatar" />
            </div>
            <div className="user-details">
              <h1 className="username">{user.username}</h1>
              <p className="email">{user.email}</p>
              <button className="logout-button" onClick={openLogoutModal}>
                Log Out
              </button>
            </div>
          </div>
          <div className="sections-container">
            <UserInfo user={user} showUserInfo={showUserInfo} toggleUserInfo={() => setShowUserInfo(!showUserInfo)} />
            <AccountSettings showAccountSettings={showAccountSettings} toggleAccountSettings={() => setShowAccountSettings(!showAccountSettings)} />
            <Notifications notifications={notifications} handleNotificationChange={handleNotificationChange} showNotifications={showNotifications} toggleNotifications={() => setShowNotifications(!showNotifications)} />
            <Privacy showPrivacy={showPrivacy} togglePrivacy={() => setShowPrivacy(!showPrivacy)} />
            <DataStorage showDataStorage={showDataStorage} toggleDataStorage={() => setShowDataStorage(!showDataStorage)} />
            <Appearance darkMode={darkMode} toggleDarkMode={toggleDarkMode} showAppearance={showAppearance} toggleAppearance={() => setShowAppearance(!showAppearance)} />
          </div>
        </>
      )}
      {showLogoutModal && (
        <div className="logout-modal">
          <div className="logout-modal-content">
            <h2>Confirm Logout</h2>
            <p>Are you sure you want to log out?</p>
            <button onClick={handleLogout}>Yes, Log Out</button>
            <button onClick={closeLogoutModal}>Cancel</button>
          </div>
        </div>
      )}
      <ToastContainer />
    </div>
  );
}

export default UserProfile;
