import React, { useEffect, useState } from "react";
import "../../../assets/style/ServicesStyle/ChatServicesStyle/ChatList.css";
import { fetchFriends } from './API/FriendsAPI';

const ChatList = ({ selectedUser, handleUserSelect }) => {
  const [friends, setFriends] = useState([]);
  const [loading, setLoading] = useState(true);

  const demoUsers = [
    {
      id: 1,
      name: "John Doe",
      lastMessage: "Hey, how are you?",
      time: "2:30 PM",
      avatar: "/images/avatar/avatar-Alice.png",
      unread: 2,
      active: true,
      status: "seen",
      addedAt: new Date('2023-10-01'),
      lastMessageTime: new Date('2023-10-01T14:30:00'),
    },
    {
      id: 2,
      name: "Jane Smith",
      lastMessage: "Can we meet tomorrow?",
      time: "Yesterday",
      avatar: "/images/avatar/avatar-Bob.png",
      unread: 0,
      active: false,
      status: "delivered",
      addedAt: new Date('2023-09-15'),
      lastMessageTime: new Date('2023-09-30T10:00:00'),
    },
    {
      id: 3,
      name: "Bob Johnson",
      lastMessage: "Thanks for your help!",
      time: "2 days ago",
      avatar: "/images/avatar/avatar-Charlie.png",
      unread: 5,
      active: true,
      status: "sent",
      addedAt: new Date('2023-08-20'),
      lastMessageTime: new Date('2023-09-29T09:00:00'),
    },
  ];

  useEffect(() => {
    const token = localStorage.getItem("token");
    const fetchFriendsData = async () => {
      try {
        const data = await fetchFriends(token);
        setFriends(data.friends || []);
      } catch (error) {
        console.error("Error fetching friends:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchFriendsData();
  }, []);

  // Combine demo users and fetched friends
  const combinedUsers = [...friends, ...demoUsers];

  // Sort users by lastMessageTime first, then by addedAt in descending order
  const sortedUsers = combinedUsers.sort((a, b) => {
    if (b.lastMessageTime - a.lastMessageTime !== 0) {
      return b.lastMessageTime - a.lastMessageTime; // Sort by last message time
    }
    return b.addedAt - a.addedAt; // Sort by added date if last message times are equal
  });

  if (loading) {
    return <div>Loading friends...</div>;
  }

  return (
    <div className="chat-sidebar">
      <ul className="chat-list">
        {sortedUsers.map((user) => (
          <li
            key={user.id}
            className={`chat-list-item ${selectedUser?.id === user.id ? "active" : ""}`}
            onClick={() => handleUserSelect(user)}
          >
            <div className="chat-item-content">
              <div className="avatar-container">
                <img src={user.avatar} alt={user.name} className="chat-avatar" />
              </div>
              <div className="chat-info">
                <div className="chat-header">
                  <span className={`chat-name ${user.unread > 0 ? "unread" : ""}`}>
                    {user.name}
                  </span>
                  <span className="chat-time">{user.time}</span>
                </div>
                <div className="chat-footer">
                  <span className="chat-last-message">
                    {user.lastMessage || "No messages yet."} {/* Show empty message for demo users */}
                  </span>
                  {user.unread > 0 && (
                    <span className="unread-badge">{user.unread}</span>
                  )}
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ChatList;
