import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../utils/ReactToastifyCustom.css';
import '../utils/style/home.css';

function InactiveAccountPopup() { // Removed the `token` prop since it's generated dynamically
    const navigate = useNavigate();

    const handleActivateAccount = () => {
        // Step 1: Generate the token
        fetch('http://localhost:8080/auth/generate-token', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('authToken')}`, // Assuming you have an auth token stored
            },
        })
        .then(response => response.json())
        .then(result => {
            if (result.success) {
                const token = result.token;

                // Step 2: Verify the generated token
                fetch('http://localhost:8080/auth/verify-token', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': token,
                    },
                })
                .then(response => response.json())
                .then(verifyResult => {
                    if (verifyResult.success) {
                        // Navigate to VerifyCodeConfirmAccount with token
                        navigate('/verify-email', { state: { token } });
                    } else {
                        // Handle invalid token case
                        alert('Invalid or expired token. Please log in again.');
                    }
                })
                .catch(err => {
                    alert('Network error. Please try again.');
                });

            } else {
                alert('Failed to generate token. Please try again.');
            }
        })
        .catch(err => {
            alert('Network error. Please try again.');
        });
    };

    return (
        <div className="popup-overlay">
            <div className="inactive-account-message-container">
                <h1>Your account is not confirmed yet.</h1>
                <p>Please check your email to confirm your account.</p>
                <button className="confirm-account-button" onClick={handleActivateAccount}>
                    Activate Account
                </button>
            </div>
        </div>
    );
}

export default InactiveAccountPopup;
