import React, { useState, useRef, useEffect } from "react";
import "../../../assets/style/ExtensionStyle/MessageInput.css";

const MAX_CHARS = 5000;

const MessageInput = ({ message, setMessage, handleSendMessage }) => {
  const [isFocused, setIsFocused] = useState(false);
  const [showOptions, setShowOptions] = useState(false);
  const textareaRef = useRef(null);

  const handleInputChange = (e) => {
    const inputValue = e.target.value;
    if (inputValue.length <= MAX_CHARS) {
      setMessage(inputValue);
    }
  };

  const toggleOptions = () => {
    setShowOptions(!showOptions);
  };

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = "5px";
      const scrollHeight = textareaRef.current.scrollHeight;
      textareaRef.current.style.height = `${Math.min(scrollHeight, 200)}px`;
    }
  }, [message]);

  return (
    <div className={`prompt-container ${isFocused ? "focused" : ""}`}>
      <div className="prompt-input-wrapper">
        <div className="document-btn-wrapper">
          <button
            className="document-btn"
            id="add-document"
            onClick={toggleOptions}
          >
            {/* ... SVG for add document button ... */}
          </button>
          {showOptions && (
            <div className="document-options">
              {/* ... option buttons ... */}
            </div>
          )}
        </div>

        <textarea
          ref={textareaRef}
          id="message-input"
          className="message-input"
          placeholder="Enter message.."
          value={message}
          onChange={handleInputChange}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              e.preventDefault();
              handleSendMessage();
            }
          }}
        />

        <button
          className={`generate-send-btn ${!message.trim() ? 'deactivated' : ''}`}
          id="generate-send-btn"
          title={message.trim() ? "Send Message" : "Generate"}
          onClick={handleSendMessage}
          disabled={!message.trim()}
        >
          {/* ... SVG for send button ... */}
        </button>
      </div>
    </div>
  );
};

export default MessageInput;
