import React from "react";
import "../assets/style/ExtensionStyle/AvatarUploadPopup.css";

const AvatarUploadPopup = ({ onClose }) => {
  return (
    <div className="avatar-upload-popup-overlay">
      <div className="avatar-upload-popup">
        <p className="popup-title">Change Avatar</p>
        <div className="popup-options">
          <button className="popup-option-button">Upload</button>
          <button className="popup-option-button">R</button>
          <button className="popup-option-button">Select Avatar</button>
          <button className="popup-option-button" onClick={onClose}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default AvatarUploadPopup;
