import React from 'react';
import './Popup.css';

const Popup = ({ title, content, onClose }) => {
    return (
        <div className="popup-overlay fade-in-overlay"> {/* Added fade-in overlay animation */}
            <div className="popup-content fade-in-center"> {/* Added smooth animation */}
                <button className="close-button" onClick={onClose}>
                    <i className="fa fa-times close-icon" aria-hidden="true"></i>
                </button>
                <div>{content}</div>
            </div>
        </div>
    );
};

export default Popup;
