import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/style/profile.css'; // Ensure you have this file

function UserProfile() {
    const [user, setUser] = useState({});
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchUserProfile();
    }, []);

    const fetchUserProfile = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:8080/Users/me', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            const result = await response.json();
            if (response.ok) {
                setUser(result);
            } else {
                handleError(result.message || 'Failed to fetch user data. Please try again.');
                navigate('/login'); // Redirect if the fetch fails
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
            navigate('/login'); // Redirect if there is a network error
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = async () => {
        try {
            const response = await fetch("http://localhost:8080/auth/logout", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({ refreshToken: localStorage.getItem('refreshToken') })
            });
            if (response.ok) {
                localStorage.removeItem('token');
                localStorage.removeItem('refreshToken');
                localStorage.removeItem('loggedInUser');
                handleSuccess('Logged out successfully! Redirecting...');
                setTimeout(() => navigate('/login'), 500);
            } else {
                handleError('Failed to log out. Please try again.');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    return (
        <div className="user-profile-container-outer-flex-direction-column-align-items-center-justify-content-center-width-height-margin-background-color-paddingx-border-radius-box-shadow">
            {loading ? (
                <div className="loading-overlay-profile-container-cover-with-spinner-centered-background-semi-transparent">
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            ) : (
                <>
                    <div className="user-image-name-container-flex-direction-column-align-items-center-background-border-radius-padding-margin-box-shadow-transition">
                        <div className="avatar-image-container-flex-shrink-width-height-border-radius-background-color-margin-cursor-transition">
                            <img src='/avater.png' className='profilePicture' alt='Profile' />
                        </div>
                        <h1 className="user-name-text-font-size-color-font-weight-margin-line-height">{user.name}</h1>
                    </div>
                    <div className="profile-details-container-flex-direction-column-align-items-start-background-color-border-radius-padding-box-shadow-width-max-width-margin-bottom">
                        <ul className="profile-details-list-remove-list-style-padding-margin-width">
                            <li className="profile-details-list-item-margin-font-size-color">{`Name: ${user.name}`}</li>
                            <li className="profile-details-list-item-margin-font-size-color">{`Email: ${user.email}`}</li>
                            <li className="profile-details-list-item-margin-font-size-color">{`Username: ${user.username}`}</li>
                            <li className="profile-details-list-item-margin-font-size-color">{`Joined: ${new Date(user.createdAt).toLocaleDateString()}`}</li>
                        </ul>
                    </div>
                </>
            )}
            <div className="profile-actions-container-flex-direction-row-justify-content-center-gap">
                <button className="profile-action-button-padding-border-radius-border-cursor-font-size-background-color-color-transition" onClick={() => navigate(-1)}>Back</button>
                <button className="profile-action-button-padding-border-radius-border-cursor-font-size-background-color-color-transition" onClick={handleLogout}>Logout</button>
            </div>
            <ToastContainer />
        </div>
    );
}

export default UserProfile;
