// pages/ResetPassword.js
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../../utils/ReactToastify';
import '../../utils/ReactToastifyCustom.css';
import '../../utils/style/ResetPassword.css';

function ResetPassword() {
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const { state } = useLocation();
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (password !== confirmPassword) {
            handleError('Passwords do not match');
            return;
        }

        try {
            const response = await fetch('http://localhost:8080/verification/forgot-password/reset-password', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email: state.email, newPassword: password }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                navigate('/login');
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    return (
        <div className="reset-password-container container">
            <h1>Reset Password</h1>
            <form onSubmit={handleSubmit}>
                <input
                    className="otpField"
                    type="password"
                    placeholder="Enter your new password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <input
                    className="otpField"
                    type="password"
                    placeholder="Confirm your new password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                />
                <button className="Resetpassword-button" type="submit">Reset Password</button>
            </form>
            <ToastContainer />
        </div>
    );
}

export default ResetPassword;
