import React, { useState, useEffect } from 'react';
import { Navigate, Route, Routes } from 'react-router-dom';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Home from './pages/Home';
import FindUserForgotPassword from './components/ForgotPassword/FindUserForgotPassword';
import SentOtpForgotPassword from './components/ForgotPassword/VerificationCodeForgotPassword';
import ResetPassword from './components/ForgotPassword/ResetPassword';
import RefreshHandler from './utils/RefreshHandler';
import ProtectedRoute from './utils/ProtectedRoute';
import 'react-toastify/dist/ReactToastify.css';
import VerifyCodeConfirmAccount from './components/VerifyCodeConfirmAccount';
import { checkAuthentication } from './utils/RefreshHandler';
import LoadingOverlay from './components/LoadingOverlay';

function App() {
	const [isAuthenticated, setIsAuthenticated] = useState(false);
	const [isLoading, setIsLoading] = useState(true);

	useEffect(() => {
		const verifyAuth = async () => {
			const authStatus = await checkAuthentication();
			setIsAuthenticated(authStatus);
			setIsLoading(false);
		};
		verifyAuth();
	}, []);

	if (isLoading) {
		return <LoadingOverlay loading={true} fadeOut={false} />;
	}

	return (
		<div className="App">
			<RefreshHandler setIsAuthenticated={setIsAuthenticated} />
			<Routes>
				<Route path='/' element={isAuthenticated ? <Navigate to="/home" /> : <Navigate to="/login" />} />
				<Route path='/login' element={<Login setIsAuthenticated={setIsAuthenticated} />} />
				<Route path='/signup' element={<Signup />} />
				<Route path="/verify-email" element={<ProtectedRoute element={VerifyCodeConfirmAccount} isAuthenticated={isAuthenticated} />} />
				<Route path='/home' element={<ProtectedRoute element={Home} isAuthenticated={isAuthenticated} />} />
				<Route path='/find-user' element={<FindUserForgotPassword />} />
				<Route path='/forgot-password/verification-code' element={<SentOtpForgotPassword />} />
				<Route path='/forgot-password/reset-password' element={<ResetPassword />} />
			</Routes>
		</div>
	);
}

export default App;
