import React, { useState, useEffect } from "react"; // Ensure useEffect is imported
import { handleError, handleSuccess } from "../utils/ReactToastify";
import '../assets/style/styleutils/ReactToastifyCustom.css';
import "../assets/style/PagesStyle/Profile.css";
import { useNavigate } from "react-router-dom";
import { logoutUser, deleteUser } from '../utils/ApiService';

// Function to clear all cookies
const clearCookies = () => {
    const cookies = document.cookie.split("; ");
    for (let cookie of cookies) {
        const eqPos = cookie.indexOf("=");
        const name = eqPos > -1 ? cookie.substr(0, eqPos) : cookie;
        document.cookie = name + "=;expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/";
    }
};

function UserProfile({ user, onClose }) {
    const navigate = useNavigate();
    const [showLogoutModal, setShowLogoutModal] = useState(false);
    const [isModalActive, setIsModalActive] = useState(false);
    const [fadeInClass, setFadeInClass] = useState('');
    const [showProfile, setShowProfile] = useState(false); // New state for profile visibility
    const [scaleInClass, setScaleInClass] = useState(''); // State for scale-in animation

    const handleLogout = async () => {
        try {
            const response = await logoutUser();
            if (response.status === 200) {
                handleSuccess("Logged out successfully!");
                // Clear local storage
                localStorage.removeItem("token");
                localStorage.removeItem("refreshToken");
                localStorage.removeItem("loggedInUser");
                // Clear cookies
                clearCookies();
                setTimeout(() => navigate("/login"), 500);
            } else {
                handleError("Failed to log out. Please try again.");
            }
        } catch (err) {
            handleError("Network error. Please check your connection and try again.");
        }
    };

    const openLogoutModal = () => {
        setShowLogoutModal(true);
        setTimeout(() => {
            setIsModalActive(true);
            setFadeInClass('fade-in'); // Trigger animation
        }, 10);
    };

    const closeLogoutModal = () => {
        setIsModalActive(false);
        setFadeInClass(''); // Reset animation class
        setTimeout(() => setShowLogoutModal(false), 300);
    };

    useEffect(() => {
        if (showLogoutModal) {
            setFadeInClass('fade-in'); // Ensure fade-in class is set when modal is shown
        }
    }, [showLogoutModal]);

    const handleDeleteUser = async () => {
        try {
            const response = await deleteUser(user._id);
            const data = await response.json();
            if (response.ok) {
                handleSuccess(data.message);
                localStorage.removeItem("token");
                localStorage.removeItem("refreshToken");
                localStorage.removeItem("loggedInUser");
                setTimeout(() => navigate("/login"), 500);
            } else {
                handleError(data.message || "Failed to delete user. Please try again.");
            }
        } catch (err) {
            handleError("Network error. Please check your connection and try again.");
        }
    };

    const settingsItems = [
        { title: "Account", icon: "ðŸ‘¤" },
        { title: "Privacy", icon: "ðŸ”" },
        { title: "Appearance", icon: "ðŸŽ¨" },
        { title: "Notifications", icon: "ðŸ””" },
        { title: "Language", icon: "ðŸŒ" },
        { title: "Help", icon: "â“" }
    ];

    const toggleProfile = () => {
        setShowProfile(prev => !prev);
        setScaleInClass('scale-in'); // Apply scale-in animation
    };

    const closeProfile = () => {
        setScaleInClass(''); // Reset animation class
        setTimeout(() => setShowProfile(false), 300); // Delay hiding the profile container
    };

    return (
        <div>
            <button onClick={toggleProfile}>Profile</button> {/* Button to toggle profile */}
            {showProfile && (
                <div className={`profile-container ${scaleInClass}`}> {/* {{ edit_1 }} */}
                    <div className="profile-header">
                        <div className="avatar">
                            <img src='images/avatar/avatar-Alice.png' className='profile-picture' alt='Profile' />
                        </div>
                        <h1 className="user-name">{`${user.first_name || ''} ${user.last_name || ''}`}</h1>
                        <p className="user-email">{user.email}</p>
                    </div>

                    <div className="settings-grid">
                        {settingsItems.map((item, index) => (
                            <div key={index} className="settings-item">
                                <span className="settings-icon">{item.icon}</span>
                                <span className="settings-title">{item.title}</span>
                            </div>
                        ))}
                    </div>

                    <div className="logout-container">
                        <button className="logout-button" onClick={openLogoutModal}>
                            Logout
                        </button>
                    </div>

                    {showLogoutModal && (
                        <div className={`logout-modal-overlay ${isModalActive ? 'active fade-in' : ''}`}>
                            <div className="logout-modal scale-in">
                                <p>Are you sure you want to logout?</p>
                                <div className="modal-actions">
                                    <button className="modal-cancel-button" onClick={closeLogoutModal}>
                                        Cancel
                                    </button>
                                    <button className="modal-logout-button" onClick={handleLogout}>
                                        Logout
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default React.memo(UserProfile);
