import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/style/home.css';
import '../utils/loading.css';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState({});
    const [loading, setLoading] = useState(true);
    const [fadeOut, setFadeOut] = useState(false);
    const [fadeIn, setFadeIn] = useState(true);
    const [showConfirmModal, setShowConfirmModal] = useState(false); // State for modal visibility
    const navigate = useNavigate();

    useEffect(() => {
        fetchLoggedInUser();

        const timeoutId = setTimeout(() => {
            setFadeIn(false);
        }, 500);

        return () => clearTimeout(timeoutId);
    }, []);

    const fetchLoggedInUser = async () => {
        setLoading(true);
        try {
            const url = "http://localhost:8080/Users/me";
            const headers = {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            };
            const response = await fetch(url, headers);
            const result = await response.json();
            if (response.ok) {
                setLoggedInUser(result);
            } else {
                handleError(result.message || 'Failed to fetch user data. Please try again.');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleConfirmAccount = () => {
        if (!loggedInUser.isActive) {
            // Navigate to VerifyCodeConfirmAccount with email in state
            navigate('/verify-code-confirm-account', { state: { email: loggedInUser.email } });
        } else {
            handleError('Your account is already active.');
        }
    };

    return (
        <div className={`home-container container ${fadeIn ? 'fade-in' : ''}`}>
            {loading ? (
                <div className="loading-overlay">
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            ) : (
                <>
                    <div className="user-image-icon-container">
                        <Link to="/user-profile" className='pages-link'>
                            <div className="user-icon-image">
                                <img src='/avater.png' className='profilePicture' alt='Profile' />
                            </div>
                        </Link>
                        <h1 className="user-name">{loggedInUser.name}</h1>
                    </div>
                    {/* Removed the logout button and its logic */}
                    {!loggedInUser.isActive && (
                        <button onClick={handleConfirmAccount} className="confirm-account-button">
                            Confirm Account
                        </button>
                    )}
                </>
            )}
            <ToastContainer />
        </div>
    );
}

export default Home;
