import React from 'react';
import '../../../assets/style/ServicesStyle/ChatServicesStyle/MessageReactions.css';

const MessageReactions = ({ messageId, reaction, showReactions, onReaction, onToggleReactionMenu }) => {
  return (
    <div className="message-reaction-container">
      {reaction ? (
        <span 
          className="reaction" 
          onClick={() => onToggleReactionMenu(messageId)}
        >
          {reaction}
        </span>
      ) : (
        <button
          onClick={() => onToggleReactionMenu(messageId)}
          className="add-reaction-btn"
        >
          ðŸ˜€
        </button>
      )}
      {showReactions && (
        <div className="reaction-menu">
          <button onClick={() => onReaction(messageId, "like")} className="reaction-btn">
            <img src="/images/icon/like-reaction.svg" alt="Like" className="reaction-icon" />
          </button>
          <button onClick={() => onReaction(messageId, "love")} className="reaction-btn">
            <img src="/images/icon/love-reaction.svg" alt="Love" className="reaction-icon" />
          </button>
          <button onClick={() => onReaction(messageId, "haha")} className="reaction-btn">
            <img src="/images/icon/haha-reaction.svg" alt="Haha" className="reaction-icon" />
          </button>
          <button onClick={() => onReaction(messageId, "wow")} className="reaction-btn">
            <img src="/images/icon/wow-reaction.svg" alt="Wow" className="reaction-icon" />
          </button>
          <button onClick={() => onReaction(messageId, "sad")} className="reaction-btn">
            <img src="/images/icon/sad-reaction.svg" alt="Sad" className="reaction-icon" />
          </button>
          <button onClick={() => onReaction(messageId, "care")} className="reaction-btn">
            <img src="/images/icon/care-reaction.svg" alt="Care" className="reaction-icon" />
          </button>
          <button onClick={() => onReaction(messageId, "angry")} className="reaction-btn">
            <img src="/images/icon/angry-reaction.svg" alt="Angry" className="reaction-icon" />
          </button>
        </div>
      )}
    </div>
  );
};

export default MessageReactions;
