//Signup.js

import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/style/animations.css';
import '../utils/loading.css';

function Signup() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [fadeIn, setFadeIn] = useState(true);
    const [fadeOut, setFadeOut] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const timeoutId = setTimeout(() => {
            setFadeIn(false);
        }, 500);
        return () => clearTimeout(timeoutId);
    }, []);

const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
        handleError('Passwords do not match');
        return;
    }

    setLoading(true);
    setFadeOut(false);

    setTimeout(async () => {
        setFadeOut(true);
        setTimeout(async () => {
            try {
                // Signup request
                const signupResponse = await fetch("http://localhost:8080/auth/signup", {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ name, email, password }),
                });
                const signupResult = await signupResponse.json();
                
                if (signupResponse.ok) {
                    // Log in the user immediately after signup
                    const loginResponse = await fetch("http://localhost:8080/auth/login", {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ email, password }),
                    });
                    const loginResult = await loginResponse.json();

                    if (loginResponse.ok) {
                        localStorage.setItem('token', loginResult.accessToken);
                        localStorage.setItem('refreshToken', loginResult.refreshToken);
                        localStorage.setItem('loggedInUser', JSON.stringify(loginResult));
                        handleSuccess('Signup and login successful!');
                        setTimeout(() => navigate('/home'), 500);
                    } else {
                        handleError(loginResult.message || 'Login failed. Please try again.');
                    }
                } else {
                    handleError(signupResult.message || 'Signup failed. Please try again.');
                }
            } catch (err) {
                handleError('Network error. Please check your connection and try again.');
            } finally {
                setLoading(false);
            }
        }, 250);
    }, 1000);
};


    return (
        <div className={`signup-container container ${fadeIn ? 'fade-in' : ''}`}>
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Sign up</h1>
            <form onSubmit={handleSubmit}>
                <div className='form-group'>
                    <input
                        type='text'
                        id='name'
                        placeholder=''
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                    <label htmlFor='name' className='form-label'>Name</label>
                </div>
                <div className='form-group'>
                    <input
                        type='email'
                        id='email'
                        placeholder=''
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <label htmlFor='email' className='form-label'>Email</label>
                </div>

                <div className='form-group-container'>
                    <div className='form-group'>
                        <input
                            type='password'
                            id='password'
                            placeholder=''
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <label htmlFor='password' className='form-label'>Password</label>
                    </div>

                    <div className='form-group'>
                        <input
                            type='password'
                            id='confirmPassword'
                            placeholder=''
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            required
                        />
                        <label htmlFor='confirmPassword' className='form-label'>Confirm Password</label>
                    </div>
                </div>

                <button className='signup-button' type='submit'>Sign up</button>
                <span>Already have an account? <Link to="/login" className='pages-link'>Login</Link></span>
            </form>

            <span className="terms">
                By creating an account, you agree to our 
                <Link to="/terms" className='terms-pages-link'> Terms of Use</Link>, 
                <Link to="/privacy-policy" className='terms-pages-link'> Privacy Policy</Link>.
            </span>

            <ToastContainer />
            {loading && (
                <div className={`loading-overlay ${fadeOut ? 'hidden' : ''}`}>
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            )}
        </div>
    );
}

export default Signup;



