// pages/SentOtpForgotPassword.js
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { handleError, handleSuccess, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';

function SentOtpForgotPassword() {
    const location = useLocation();
    const navigate = useNavigate();
    const [otp, setOtp] = useState('');
    const [email] = useState(location.state?.email || '');
    const [timer, setTimer] = useState(30);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const interval = setInterval(() => {
            setTimer((prev) => (prev > 0 ? prev - 1 : 0));
        }, 1000);
        return () => clearInterval(interval);
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);

        try {
            const response = await fetch('http://localhost:8080/auth/reset-password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, otp }),
            });

            const result = await response.json();
            if (response.ok) {
                navigate('/reset-password');
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    const resendOtp = async () => {
        try {
            const response = await fetch('http://localhost:8080/auth/send-otp-forgot-password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email }),
            });

            const result = await response.json();
            if (response.ok) {
                handleSuccess('OTP resent to your email');
                setTimer(30);
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    return (
        <div className="otp-container">
            <h1>Enter OTP</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type='text'
                    placeholder='Enter OTP'
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    required
                />
                <button type='submit' disabled={timer === 0}>Verify OTP</button>
                {timer === 0 && <button type='button' onClick={resendOtp}>Resend OTP</button>}
                <div>Timer: {timer}s</div>
            </form>
            <ToastContainer />
            {loading && <div>Loading...</div>}
        </div>
    );
}

export default SentOtpForgotPassword;
