// Home.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { apiRequest, refreshToken } from '../utils/api';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

const Home = () => {
    const [loggedInUser, setLoggedInUser] = useState({});
    const [loading, setLoading] = useState(false);
    const [fadeOut, setFadeOut] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        fetchLoggedInUser();
    }, []);

    const fetchLoggedInUser = async () => {
        setLoading(true);
        try {
            const user = await apiRequest('/Users/me');
            setLoggedInUser(user);
        } catch (error) {
            handleError(error.message || 'Failed to fetch user data');
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = async () => {
        setLoading(true);
        setFadeOut(false);

        try {
            await apiRequest('/auth/logout', 'POST');
            localStorage.removeItem('token');
            localStorage.removeItem('refreshToken');
            handleSuccess('Logged out successfully');
            setTimeout(() => navigate('/login'), 250);
        } catch (error) {
            handleError(error.message || 'Logout failed');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container">
            <h1>Welcome {loggedInUser.name}</h1>
            <div>
                {loading ? (
                    <div className={`loading-overlay ${fadeOut ? 'hidden' : ''}`}>
                        <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                    </div>
                ) : (
                    <ul>
                        <li>Name: {loggedInUser.name}</li>
                        <li>Email: {loggedInUser.email}</li>
                        <li>Joined: {new Date(loggedInUser.createdAt).toLocaleDateString()}</li>
                    </ul>
                )}
            </div>

            <button onClick={handleLogout}>Logout</button>
        </div>
    );
};

export default Home;
