// Home.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState({});
    const [loading, setLoading] = useState(false);
    const [fadeOut, setFadeOut] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        fetchLoggedInUser();
    }, []);

    const handleLogout = async () => {
        setLoading(true);
        setFadeOut(false);

        try {
            const url = "http://localhost:8080/auth/logout";
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({ email: loggedInUser.email })
            });
            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                localStorage.removeItem('token');
                localStorage.removeItem('loggedInUser');
                navigate('/login');
            } else {
                handleError(result.message || 'An error occurred');
            }
        } catch (err) {
            handleError(err.message || 'An error occurred');
        } finally {
            setFadeOut(true);
            setTimeout(() => {
                setLoading(false);
            }, 250);
        }
    };

    const fetchLoggedInUser = async () => {
        setLoading(true);
        try {
            const url = "http://localhost:8080/Users/me";
            const headers = {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            };
            const response = await fetch(url, headers);
            const result = await response.json();
            if (response.ok) {
                setLoggedInUser(result);
            } else {
                handleError(result.message || 'An error occurred');
            }
        } catch (err) {
            handleError(err.message || 'An error occurred');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='container'>
            <h1>Welcome {loggedInUser.name}</h1>
            <div>
                {loading ? (
                    <div className={`loading-overlay ${fadeOut ? 'hidden' : ''}`}>
                        <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                    </div>
                ) : (
                    <ul>
                        <li>Name: {loggedInUser.name}</li>
                        <li>Email: {loggedInUser.email}</li>
                        <li>Joined: {new Date(loggedInUser.createdAt).toLocaleDateString()}</li>
                    </ul>
                )}
            </div>

            <button onClick={handleLogout}>Logout</button>

            <ToastContainer />
        </div>
    );
}

export default Home;
