import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../../utils/ReactToastify';
import '../../utils/ReactToastifyCustom.css';
import '../../utils/style/SentOtpForgotPassword.css';

const OtpInput = ({ length = 6, onOtpSubmit = () => { } }) => {
    const [otp, setOtp] = useState(new Array(length).fill(""));
    const inputRefs = useRef([]);

    useEffect(() => {
        if (inputRefs.current[0]) {
            inputRefs.current[0].focus();
        }
    }, []);

    const handleChange = (index, e) => {
        const value = e.target.value;
        if (isNaN(value) || value.length > 1) return;

        const newOtp = [...otp];
        newOtp[index] = value.slice(-1);
        setOtp(newOtp);

        const combinedOtp = newOtp.join("");
        if (combinedOtp.length === length) {
            onOtpSubmit(combinedOtp);
        }

        if (value && index < length - 1) {
            inputRefs.current[index + 1].focus();
        }
    };

    const handleClick = (index) => {
        inputRefs.current[index].setSelectionRange(1, 1);
        inputRefs.current[index].select();
    };

    const handleKeyDown = (index, e) => {
        switch (e.key) {
            case "Backspace":
                if (!otp[index] && index > 0) {
        inputRefs.current[index].setSelectionRange(1, 1);
                    inputRefs.current[index - 1].focus();
                }
                break;
            case "ArrowLeft":
                if (index > 0) {
        inputRefs.current[index].setSelectionRange(1, 1);

                    inputRefs.current[index - 1].focus();
                }
                break;
            case "ArrowRight":
                if (index < length - 1) {
        inputRefs.current[index].setSelectionRange(0, 0);

                    inputRefs.current[index + 1].focus();
                }
                break;
            case "Home":
                inputRefs.current[index].setSelectionRange(1, 1);
                inputRefs.current[0].focus();
                break;
            case "End":
                inputRefs.current[index].setSelectionRange(1, 1);
                inputRefs.current[length - 1].focus();
                break;
            case "Enter":
                if (index < length - 1) {
                    inputRefs.current[index].setSelectionRange(1, 1);
                    inputRefs.current[index + 1].focus();
                } else {
                    const combinedOtp = otp.join("");
                    onOtpSubmit(combinedOtp);
                }
                break;
            default:
                break;
        }
    };

    const handlePaste = (e) => {
        e.preventDefault();
        const pasteData = e.clipboardData.getData('text').slice(0, length);
        if (pasteData.length === length) {
            setOtp(pasteData.split(''));
            onOtpSubmit(pasteData);
        }
    };

    return (
        <div className="otp-input-wrapper">
            {otp.map((value, index) => (
                <input
                    key={index}
                    type="text"
                    ref={(input) => (inputRefs.current[index] = input)}
                    value={value}
                    onChange={(e) => handleChange(index, e)}
                    onKeyDown={(e) => handleKeyDown(index, e)}
                    onPaste={(e) => handlePaste(e)}
                    className="otpField"
                    maxLength={1}
                    onClick={() => handleClick(index)}
                />
            ))}
        </div>
    );
};

function SentOtpForgotPassword() {
    const [otp, setOtp] = useState('');
    const [timer, setTimer] = useState(30);
    const [isResendEnabled, setIsResendEnabled] = useState(false);
    const { state } = useLocation();
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:8080/verification/forgot-password/verify-otp', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email: state.email, otp }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                navigate('/forgot-password/reset-password', { state: { email: state.email } });
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    const handleResendOtp = async () => {
        try {
            // Code to resend OTP
            // Add your resend OTP logic here
            setIsResendEnabled(false);
        } catch (err) {
            handleError('Failed to resend OTP. Please try again.');
        }
    };

    useEffect(() => {
        if (timer === 0) setIsResendEnabled(true);
        const countdown = setInterval(() => {
            setTimer((prev) => (prev > 0 ? prev - 1 : 0));
        }, 1000);

        return () => clearInterval(countdown);
    }, [timer]);

    return (
        <div className="otp-wrapper">
            <h1 className="otp-title">Verify OTP</h1>
            <div className="otp-timer">
                Time Remaining: {timer}s
            </div>
            <form onSubmit={handleSubmit}>
                <OtpInput length={6} onOtpSubmit={(otp) => setOtp(otp)} />
                <div className="otp-buttons">
                    <button
                        type="submit"
                        className={`otp-btn ${timer === 0 ? 'resend' : 'verify'}`}
                        disabled={timer > 0 && !isResendEnabled}
                        onClick={timer === 0 ? handleResendOtp : undefined}
                    >
                        {timer === 0 ? 'Resend OTP' : 'Verify OTP'}
                    </button>
                </div>
            </form>
            <ToastContainer />
        </div>
    );
}

export default SentOtpForgotPassword;
