// Home.js


import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false); // Add loading state
    const [fadeOut, setFadeOut] = useState(false); // State for fade-out animation
    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
    }, []);

    const handleLogout = () => {
        setLoading(true); // Start loading
        setFadeOut(false); // Reset fade-out state

        // Show loading overlay and fade out after 3 seconds
        setTimeout(() => {
            setFadeOut(true); // Start fade-out
            setTimeout(() => {
                localStorage.removeItem('token');
                localStorage.removeItem('loggedInUser');
                handleSuccess('User logged out successfully');
                navigate('/login');
            }, 250); // Time to match CSS fade-out duration
        }, 1000); // Duration for loading spinner
    };

const fetchUsers = async () => {
    setLoading(true); // Start loading
    try {
        const token = localStorage.getItem('token'); // Get token from localStorage
        if (!token) {
            handleError('Unauthorized: No token found');
            navigate('/login');
            return;
        }

        const url = "http://localhost:8080/Users";
        const headers = {
            'Authorization': `Bearer ${token}`, // Make sure the token is sent as 'Bearer <token>'
            'Content-Type': 'application/json'
        };
        
        const response = await fetch(url, { headers });

        if (response.status === 403) {
            handleError('Forbidden: Access denied');
            navigate('/login'); // Redirect to login if unauthorized
            return;
        }

        const result = await response.json();
        setUsers(result);
    } catch (err) {
        handleError(err.message || 'An error occurred');
    } finally {
        setLoading(false); // End loading
    }
};


    useEffect(() => {
        fetchUsers();
    }, []);

    return (
        <div className='container'>
            <h1>Welcome {loggedInUser}</h1>
            <div>
                {users.length > 0 ? (
                    users.map((user, index) => (
                        <ul key={index}>
                            <li>Name: {user.name}</li>
                            <li>Email: {user.email}</li>
                            <li>Joined: {new Date(user.createdAt).toLocaleDateString()}</li>
                        </ul>
                    ))
                ) : (
                    <p>No users found</p>
                )}
            </div>

            <button onClick={handleLogout}>Logout</button>

            <ToastContainer />
            
            {loading && (
                <div className={`loading-overlay ${fadeOut ? 'hidden' : ''}`}>
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            )}
        </div>
    );
}

export default Home;
