import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';
import '../utils/style/animations.css';

function Signup() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [fadeIn, setFadeIn] = useState(true);
    const [fadeOut, setFadeOut] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const timeoutId = setTimeout(() => {
            setFadeIn(false);
        }, 500);
        return () => clearTimeout(timeoutId);
    }, []);

    useEffect(() => {
        const refreshToken = localStorage.getItem('refreshToken');
        if (refreshToken) {
            // Attempt auto-login using the refresh token
            (async () => {
                try {
                    const response = await fetch("http://localhost:8080/auth/refresh-token", {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ refreshToken }),
                    });
                    const result = await response.json();
                    if (response.ok) {
                        localStorage.setItem('token', result.accessToken);
                        localStorage.setItem('refreshToken', result.refreshToken);
                        localStorage.setItem('loggedInUser', JSON.stringify(result));
                        handleSuccess('Auto-login successful!');
                        navigate('/home');
                    } else {
                        handleError(result.message || 'Failed to auto-login.');
                    }
                } catch (err) {
                    handleError('Network error. Please check your connection and try again.');
                }
            })();
        }
    }, [navigate]);

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (password !== confirmPassword) {
            handleError('Passwords do not match');
            return;
        }

        setLoading(true);
        setFadeOut(false);

        setTimeout(async () => {
            setFadeOut(true);
            setTimeout(async () => {
                try {
                    const response = await fetch("http://localhost:8080/auth/signup", {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ name, email, password }),
                    });
                    const result = await response.json();
                    if (response.ok) {
                        localStorage.setItem('token', result.accessToken);
                        localStorage.setItem('refreshToken', result.refreshToken);
                        localStorage.setItem('loggedInUser', JSON.stringify(result));
                        handleSuccess('Signup successful!');
                        setTimeout(() => navigate('/login'), 500);
                    } else {
                        handleError(result.message || 'An error occurred during signup. Please try again.');
                    }
                } catch (err) {
                    handleError('Network error. Please check your connection and try again.');
                } finally {
                    setLoading(false);
                }
            }, 250);
        }, 1000);
    };

    return (
        <div className={`signup-container container ${fadeIn ? 'fade-in' : ''}`}>
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Sign up</h1>
            <form onSubmit={handleSubmit}>
                <div className='form-group'>
                    <input
                        type='text'
                        id='name'
                        placeholder=''
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                    <label htmlFor='name' className='form-label'>Name</label>
                </div>
                <div className='form-group'>
                    <input
                        type='email'
                        id='email'
                        placeholder=''
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <label htmlFor='email' className='form-label'>Email</label>
                </div>

                <div className='form-group-container'>
                    <div className='form-group'>
                        <input
                            type='password'
                            id='password'
                            placeholder=''
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <label htmlFor='password' className='form-label'>Password</label>
                    </div>

                    <div className='form-group'>
                        <input
                            type='password'
                            id='confirmPassword'
                            placeholder=''
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            required
                        />
                        <label htmlFor='confirmPassword' className='form-label'>Confirm Password</label>
                    </div>
                </div>

                <button className='signup-button' type='submit'>Sign up</button>
                <span>Already have an account? <Link to="/login" className='pages-link'>Login</Link></span>
            </form>

            <span className="terms">
                By creating an account, you agree to our 
                <Link to="/terms" className='terms-pages-link'> Terms of Use</Link>, 
                <Link to="/privacy-policy" className='terms-pages-link'> Privacy Policy</Link>.
            </span>

            <ToastContainer />
            {loading && (
                <div className={`loading-overlay ${fadeOut ? 'hidden' : ''}`}>
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            )}
        </div>
    );
}

export default Signup;
